    private void btn_aceptarMousePressed(java.awt.event.MouseEvent evt) {                                         
        
        btn_aceptar.setBackground(color5);
        
        ScheduledExecutorService executor = Executors.newScheduledThreadPool(1);

        Runnable tarea = () -> {
           
            Calculos_Finalizados.dispose();
            btn_aceptar.setBackground(color1);
            
        };

        executor.schedule(tarea, valorTemporizador, TimeUnit.MILLISECONDS);
        executor.shutdown();
        
    }                                        

    private void btn_aceptar_errorMousePressed(java.awt.event.MouseEvent evt) {                                               
        
        btn_aceptar_error.setBackground(color5);
        
        ScheduledExecutorService executor = Executors.newScheduledThreadPool(1);

        Runnable tarea = () -> {
           
            Error.dispose();
            btn_aceptar_error.setBackground(color1);
            
        };

        executor.schedule(tarea, valorTemporizador, TimeUnit.MILLISECONDS);
        executor.shutdown();
        
    }                                              

